mapper.select:
SELECT t_user.id,t_user.name,t_user.card,t_user.phone,t_user.create_time,t_user.expiry,t_user.state,t_user.info,t_user.type_id,t_type.type_name
FROM t_user
JOIN t_type ON t_user.type_id = t_type.id


        AND
        name LIKE CONCAT('%', CONCAT(#{userSelectDTO.name}, '%'))



        AND
        card = #{userSelectDTO.card}



        AND
        type_id = #{userSelectDTO.typeId}



        AND
        state = #{userSelectDTO.state}



        AND
        t_user.create_time >= #{userSelectDTO.airTime}



        AND
        t_user.create_time <= #{userSelectDTO.stopTime}

SELECT DISTINCT FLOOR( HOUR (t_access.scan_code_time) / 2 ) *2 PeriodTime , SUM(t_qr.id = 2)OutStand1, SUM(t_qr.id = 3)IntStand1, SUM(t_qr.id = 4)OutStand2, SUM(t_qr.id = 5)IntStand2 FROM t_access LEFT JOIN t_qr ON t_qr.id = t_access.qr_id
AND t_access.scan_code_time >= #{hoursDTO.airTime}
AND t_access.scan_code_time <= #{hoursDTO.stopTime}
GROUP BY FLOOR( HOUR (t_access.scan_code_time) / 2 ) *2

SELECT DISTINCT t_type.type_name, SUM(t_qr.id = 2)OutStand1, SUM(t_qr.id = 3)IntStand1, SUM(t_qr.id = 4)OutStand2, SUM(t_qr.id = 5)IntStand2 FROM t_access LEFT JOIN t_qr ON t_qr.id = t_access.qr_id LEFT JOIN t_user ON t_access.user_id = t_user.id LEFT JOIN t_type ON t_type.id = t_user.type_id
AND t_access.scan_code_time >= #{hoursDTO.airTime}
AND t_access.scan_code_time <= #{hoursDTO.stopTime}
GROUP BY t_type.type_name

SELECT MONTH(t_access.scan_code_time) MonthTime, SUM(t_type.id = 1) teach, SUM(t_type.id = 2) student, SUM(t_type.id = 4) visit, COUNT(t_type.id = 1) headcount FROM t_access LEFT JOIN t_qr ON t_qr.id = t_access.qr_id LEFT JOIN t_user ON t_access.user_id = t_user.id LEFT JOIN t_type ON t_type.id = t_user.type_id
t_access.qr_id = #{monthDTO.id}
GROUP BY MONTH(t_access.scan_code_time)
